contracts/openforge/adminFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {AppStorage} from "../shared/storage/structs/AppStorage.sol";
import {modifiersFacet} from "../shared/utils/modifiersFacet.sol";

contract AdminFacet is modifiersFacet {
    AppStorage internal s;

    function Admin_setContract(
        string memory _contractName,
        address _contractAddress
    ) external onlyAdmin {
        s.contracts[_contractName] = _contractAddress;
    }

    function getContract(
        string memory _contractName
    ) external view returns (address) {
        return s.contracts[_contractName];
    }

    function Admin_itemSet(uint _itemId, uint _price) external onlyAdmin {
        s.items[_itemId].itemId = _itemId;
        s.items[_itemId].price = _price;
    }

    function Admin_itemGet(uint _itemId) external view returns (uint, uint) {
        return (s.items[_itemId].itemId, s.items[_itemId].price);
    }
}

contracts/openforge/registerFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {AppStorage} from "../shared/storage/structs/AppStorage.sol";
import {IERC721} from "../shared/interfaces/IERC721.sol";

contract RegisterFacet {
    AppStorage internal s;

    function Register_registerUser(string memory _name) external {
        s.users[msg.sender].name = _name;
        IERC721 SBT = IERC721(s.contracts["sbt"]);
        uint tokenId = SBT.safeMint(msg.sender, "-");
        s.users[msg.sender].tokenId = tokenId;
    }

    function Register_userGet() external view returns (string memory, uint) {
        return (s.users[msg.sender].name, s.users[msg.sender].tokenId);
    }
}

contracts/shared/interfaces/IDiamondCut..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamonds: https://eips.ethereum.org/EIPS/eip-2535
/******************************************************************************/

interface IDiamondCut {
    enum FacetCutAction {Add, Replace, Remove}
    // Add=0, Replace=1, Remove=2

    struct FacetCut {
        address facetAddress;
        FacetCutAction action;
        bytes4[] functionSelectors;
    }

    /// @notice Add/replace/remove any number of functions and optionally execute
    ///         a function with delegatecall
    /// @param _diamondCut Contains the facet addresses and function selectors
    /// @param _init The address of the contract or facet to execute _calldata
    /// @param _calldata A function call, including function selector and arguments
    ///                  _calldata is executed with delegatecall on _init
    function diamondCut(
        FacetCut[] calldata _diamondCut,
        address _init,
        bytes calldata _calldata
    ) external;

    event DiamondCut(FacetCut[] _diamondCut, address _init, bytes _calldata);
}

contracts/shared/interfaces/IDiamondLoup.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamonds: https://eips.ethereum.org/EIPS/eip-2535
/******************************************************************************/

// A loupe is a small magnifying glass used to look at diamonds.
// These functions look at diamonds
interface IDiamondLoupe {
    /// These functions are expected to be called frequently
    /// by tools.

    struct Facet {
        address facetAddress;
        bytes4[] functionSelectors;
    }

    /// @notice Gets all facet addresses and their four byte function selectors.
    /// @return facets_ Facet
    function facets() external view returns (Facet[] memory facets_);

    /// @notice Gets all the function selectors supported by a specific facet.
    /// @param _facet The facet address.
    /// @return facetFunctionSelectors_
    function facetFunctionSelectors(address _facet) external view returns (bytes4[] memory facetFunctionSelectors_);

    /// @notice Get all the facet addresses used by a diamond.
    /// @return facetAddresses_
    function facetAddresses() external view returns (address[] memory facetAddresses_);

    /// @notice Gets the facet that supports the given selector.
    /// @dev If facet is not found return address(0).
    /// @param _functionSelector The function selector.
    /// @return facetAddress_ The facet address.
    function facetAddress(bytes4 _functionSelector) external view returns (address facetAddress_);
}

